import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpenseFilter from "./NewExpense/ExpenseFilter";
import "./Experiment.css";
const Experiment = (props) => {
  const [selectedYear, setSelectedYear] = useState("2020");
  const onChangeFilterHandler = (getFilterYear) => {
    setSelectedYear(getFilterYear);
  };
  const FilteredYears = props.item.filter((expense) => {
    return expense.date.getFullYear().toString() === selectedYear;
  });
  //console.log(props.expenses);
  let expenseContent = <p>No Expense Item Found.</p>;
  if (FilteredYears.length > 0) {
    expenseContent = FilteredYears.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        date={expense.date}
        amount={expense.amount}
      />
    ));
  }
  return (
    <div className="experiment">
      <ExpenseFilter
        fixedYear={selectedYear}
        onChangeFilter={onChangeFilterHandler}
      />
      {expenseContent}
    </div>
  );
};
export default Experiment;
